import React, { Component } from 'react';
import {
  StyleSheet,
  Text,
  View,
  Alert,
  TouchableOpacity,
  ScrollView
} from 'react-native';
import CarouselPager from 'react-native-carousel-pager';

export default class Slider extends Component {
  render() {

    var i = 0;
    let horizontalPages = [];
    let verticalPages = [];
    for (let i = 0; i < 5; i++) {
      horizontalPages.push(
        <ScrollView key={'page-' + i}>
          <View
            style={{
              flex: 1,
              flexDirection: 'row',
              alignItems: 'center',
              justifyContent: 'center',
              padding: 30,
              borderRadius: 2
            }}>
            <TouchableOpacity onPress={() => this.horizontalCarousel.goToPage((i + 1) % 5)}>
              <Text style={{ color: '#666', fontSize: 60, fontWeight: 'bold' }}>{i + 1}</Text>
            </TouchableOpacity>
          </View>
        </ScrollView>
      );
      verticalPages.push(
        <ScrollView horizontal key={'page-' + i}>
          <View
            style={{
              flex: 1,
              flexDirection: 'row',
              alignItems: 'center',
              justifyContent: 'center',
              padding: 30,
              borderRadius: 2
            }}>
            <TouchableOpacity onPress={() => this.verticalCarousel.goToPage((i + 1) % 5)}>
              <Text style={{ color: '#666', fontSize: 60, fontWeight: 'bold' }}>{i + 1}{i + 1}</Text>
            </TouchableOpacity>
          </View>
        </ScrollView>
      );
    }

    return (
      <View style={styles.container}>
        <View style={{ flex: 1, flexDirection: 'row', backgroundColor: '#000', paddingTop: 10, paddingBottom: 10 }}>
          {<CarouselPager
            ref={ref => this.horizontalCarousel = ref}
            deltaDelay={10}
            pageStyle={{
              backgroundColor: '#fff',
              padding: 30,
            }}
          >{horizontalPages}</CarouselPager>}
        </View>
        
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    marginTop: 25,
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    flexDirection: 'column',
    backgroundColor: '#ccc',
    height:100,
  }
});
