import React, { useState } from "react";
import { Picker, View, Text, StyleSheet } from "react-native";

export default function Dropdown(props) {

  const [selectedcat, setSelectedcat] = useState('Select Catagory');
  const [category, setcategory] = useState([
    {
      itemName: "Samsung M20"
    },
    {
      itemName: "Nokia"
    },
    {
      itemName: "Apple"
    },
    {
      itemName: "Samsung M23"
    },
    {
      itemName: "Samsung M24"
    },
    {
      itemName: "Samsung M25"
    }
  ]);



  const onValueChangeCat = async (value) => {
    if(value ===''){
      console.log("null");
    }
    else{
      props.onValueChange()
    }
  }

  return (
    <View style={[props.style, styles.viewStyle]}>
      <View style={{ flex: 1, fontSize: 14, }}>
        <Picker
          itemStyle={styles.itemStyle}
          mode="dropdown"
          style={styles.pickerStyle}
          selectedValue={selectedcat}
          onValueChange={onValueChange.bind(this)}
        >
          <Picker.Item key={0}
            color="#808e9b"
            label='Select Catagory'
            value=''
            index={0}
          />
          {category.map((item, index) => (
            <Picker.Item key={index + 1}
              color="#808e9b"

              label={item.itemName}
              value={item.itemName}
              index={index + 1}
            />
          ))}
        </Picker>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  viewStyle: {
    // flex: 1,
    alignSelf: "center",
    flexDirection: "row",
    width: "92%",
    justifyContent: "space-between",
    alignItems: "center"
