import React, { useState } from 'react';
import {
  StyleSheet,
  Text,
  View,
  ScrollView,
  TouchableOpacity,
  TouchableWithoutFeedback,
  Image,
  ActivityIndicator,
  Dimensions,
  TextInput,

} from 'react-native';
import AppLoading from 'expo-app-loading';
import { AntDesign } from '@expo/vector-icons';
// Fonts
import { useFonts } from 'expo-font';
import User from '../Models/User';
import Constants from '../Models/Constants';
import Header from '../Components/Header';
import Dialog from '../Components/Dialog';
import GlobalMethods from '../Models/GlobalMethods';

function Photos({ photos }) {
  const imgWidth = Dimensions.get('screen').width * 0.33333;
  return (
    <View style={{}}>
      <View
        style={{
          flexDirection: 'row',
          flexWrap: 'wrap',
          alignItems: 'flex-start',
        }}>
        {photos.map((photo, index) => (
          <View key={index}>
            <Image
              style={{ width: imgWidth, height: imgWidth }}
              source={{
                uri: `https://picsum.photos/200/300?random=${index + 1}`,
              }}
            />
          </View>
        ))}
      </View>
    </View>
  );
}

function Albums(prop) {
  const [albums] = useState([
    {
      name: 'Animals',
      images: [
        'https://i.picsum.photos/id/1074/367/267.jpg?hmac=2YamGD7W1FNtp9UvAVUDdYUm44xzyHCthHqFl6jVT0M',
        'https://i.picsum.photos/id/237/367/267.jpg?hmac=9Xp8JrOngpF2E_G3tRKnJMhZu5AX8FimulIG_sLj1xg',
        'https://i.picsum.photos/id/1084/367/267.jpg?hmac=VaCZRCvuoubMR-S6bXItyxmDVwAaumZU2x1ulWE0faU',
        'https://i.picsum.photos/id/219/367/267.jpg?hmac=S8RAgXxGj5AUho8KQ0hsjW8bhy1d-WunZNm77FCqC3w',
      ],
    },
    {
      name: 'Food',
      images: [
        'https://images.unsplash.com/photo-1432139555190-58524dae6a55?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60',
        'https://images.unsplash.com/photo-1506354666786-959d6d497f1a?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60',
        'https://images.unsplash.com/photo-1460306855393-0410f61241c7?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60',
        'https://images.unsplash.com/photo-1497034825429-c343d7c6a68f?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60',
      ],
    },
  ]);
  const imgWidth = Dimensions.get('screen').width * 0.33333;
  return (

    <View style={{ flex: 1, backgroundColor: '#fff', paddingBottom: 20 }}>
      {albums.map((album, albumIndex) => (
        <ScrollView key={albumIndex} horizontal showsHorizontalScrollIndicator={false}>
          {album.images.map((img, imgIndex) => (
            <TouchableWithoutFeedback key={imgIndex} style={{ flexDirection: 'row', marginTop: 10 }}
              onPress={prop.ProductHanler}
            >
              <Image
                style={{ width: imgWidth + 50, height: imgWidth + 50 }}
                source={{ uri: img }}
              />
            </TouchableWithoutFeedback>
          ))}


          <View
            style={{
              position: 'absolute',
              bottom: 10,
              left: 10,
              backgroundColor: '#111',
              paddingHorizontal: 10,
              paddingVertical: 4,
              borderRadius: 6,
            }}>
            <Text style={{ color: '#fff', fontFamily: 'SourceSansPro-Bold', fontSize: 20 }}>
              {album.name}
            </Text>
          </View>
        </ScrollView>
      ))}
    </View>
  );
}

function Tags({ photos }) {
  const imgWidth = Dimensions.get('screen').width * 0.33333;
  return (
    <View style={{}}>
      <View
        style={{
          flexDirection: 'row',
          flexWrap: 'wrap',
          alignItems: 'flex-start',
        }}>
        {photos.map((photo, index) => (
          <View key={index}>
            <Image
              style={{ width: imgWidth, height: imgWidth }}
              source={{
                uri: `https://picsum.photos/200/300?random=${index + 100}`,
              }}
            />
          </View>
        ))}
      </View>
    </View>
  );
}

export default function Profile(props) {

  const [user, setuser] = useState({});
  const [loading, setLoading] = useState(true);
  const [ShowDialog, setShowDialog] = useState(false);
  const [UserBio, setUserBio] = useState('');

  const [loaded] = useFonts({
    SourceSansProLight: require('../assets/fonts/SourceSansPro/SourceSansPro-Light.ttf'),
    SourceSansProRegular: require('../assets/fonts/SourceSansPro/SourceSansPro-Regular.ttf'),
    SourceSansProBold: require('../assets/fonts/SourceSansPro/SourceSansPro-Bold.ttf'),
  });
  const fetching = async () => {
    let data = await GlobalMethods.fetchUseById("18875a4d-91fd-4b6c-a11b-343dd689f084");
     
    setuser(data);
    console.log(data);
    setUserBio(data.bio);
  }

  // React.useEffect(() => {
  //       fetching();

  // } ,[1]);
  const [showContent, setShowContent] = useState('Photos');

  const EditUserBio = async () => {
    if (UserBio != null && UserBio != '') {

      console.log(UserBio);

      let data = JSON.stringify({
        username: user.username,
        password: user.password,
        bio: UserBio,

      });
      let options = {
        method: 'POST',
        body: data,
        headers: {
          // Accept: 'application/json',
          // 'Content-Type': 'multipart/form-data',
          'Content-Type': 'application/json',
          'Authorization': 'Bearer e3fff09e-576b-4a08-8529-e88efd8eb5d5',
        },
      };
      let res = await fetch(Constants.EditUserBio, options);
      let resData = await res.text();
      if (resData != 'Done')
        console.log(resData);
      else
        await fetching();
    }
    else {
      console.log('UserBio is null');
    }
  }


  const GotoProducts = () => {
    props.navigation.navigate('CreateProduct');
  }
  if (loading || !loaded) {
    return (
      <AppLoading
        startAsync={fetching}
        onFinish={() => setLoading(false)}
        onError={console.warn}
      />
    );
  }
  return (
    <View style={{ flex: 1, backgroundColor: '#fff' }}>
      <Header title={user.username} CanGoBack={false} />
      {loading && <View>
        <Text>profile</Text>
      </View>}
      {user &&

        <ScrollView showsVerticalScrollIndicator={false}>

          <>
            <View>
              <Image
                style={styles.coverImage}
                blurRadius={5}
                // source={{ uri: 'https://picsum.photos/500/500?random=211' }}
                source={{ uri: 'https://randomuser.me/api/portraits/women/46.jpg' }}
              />
            </View>
            <View style={styles.profileContainer}>
              {/* Profile Details */}
              <View>
                {/* Profile Image */}
                <View style={styles.profileImageView}>
                  <Image
                    style={styles.profileImage}
                    source={{
                      uri: 'https://randomuser.me/api/portraits/women/46.jpg',
                    }}
                  />
                </View>
                {/* Profile Name and Bio */}
                <View style={styles.nameAndBioView}>
                  <Text style={styles.userFullName}>{user.firstName + " " + user.lastName}</Text>
                  <View style={{
                    flexDirection: 'row', padding: 10,
                    justifyContent: 'space-between'
                  }}>

                    <Text style={styles.userBio}>

                      {user.bio}</Text>
                    <TouchableOpacity onPress={() => setShowDialog(true)}>
                      <AntDesign name="edit" size={24} color="black" />
                    </TouchableOpacity>
                  </View>
                </View>
                {/* Posts/Followers/Following View */}
                <View style={styles.countsView}>
                  <View style={styles.countView}>
                    <Text style={styles.countNum}>13</Text>
                    <Text style={styles.countText}>Posts</Text>
                  </View>
                  <View style={styles.countView}>
                    <Text style={styles.countNum}>1246</Text>
                    <Text style={styles.countText}>Followers</Text>
                  </View>
                  <View style={styles.countView}>
                    <Text style={styles.countNum}>348</Text>
                    <Text style={styles.countText}>Following</Text>
                  </View>
                </View>
                {/* Interact Buttons View */}
                <View style={styles.interactButtonsView}>
                  <TouchableOpacity style={styles.interactButton}>
                    <Text style={styles.interactButtonText}>Follow</Text>
                  </TouchableOpacity>
                  <TouchableOpacity onPress={() => props.navigation.navigate('CreateProduct',{Userid:user})}
                    style={styles.interactButton}>
                    <Text
                      style={styles.interactButtonText}>
                      Products
                    </Text>
                  </TouchableOpacity>
                </View>
                {/* Mutual Followed By Text */}
                <View style={{ paddingHorizontal: 25, marginTop: 10 }}>
                  <Text style={{ fontFamily: 'SourceSansProRegular', fontSize: 16 }}>
                    {'Followed by '}
                    <Text style={{ fontFamily: 'SourceSansProBold' }}>john_doe </Text>
                    {'and '}
                    <Text style={{ fontFamily: 'SourceSansProBold' }}>19 others</Text>
                  </Text>
                </View>
              </View>
              {/* Profile Content */}
              <View style={{ marginTop: 20 }}>
                <View style={styles.profileContentButtonsView}>
                  <TouchableOpacity
                    style={{
                      ...styles.showContentButton,
                      borderBottomWidth: (showContent === 'Photos') ? 2 : 0,
                    }}
                    onPress={() => setShowContent('Photos')}>
                    <Text style={styles.showContentButtonText}>Photos</Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    style={{
                      ...styles.showContentButton,
                      borderBottomWidth: showContent === 'Albums' ? 2 : 0,
                    }}
                    onPress={() => setShowContent('Albums')}>
                    <Text style={styles.showContentButtonText}>Albums</Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    style={{
                      ...styles.showContentButton,
                      borderBottomWidth: showContent === 'Tags' ? 2 : 0,
                    }}
                    onPress={() => setShowContent('Tags')}>
                    <Text style={styles.showContentButtonText}>Tags</Text>
                  </TouchableOpacity>
                </View>
                {showContent === 'Photos' ? (
                  <Photos photos={new Array(13).fill(1)} />
                ) : showContent === 'Albums' ? (
                  <Albums ProductHanler={GotoProducts} />
                ) : (
                  <Tags photos={new Array(23).fill(1)} />
                )}
              </View>
            </View>
          </>
          <Dialog visible={ShowDialog} title="Edit Your Bio" onClose={() => setShowDialog(false)} OnSubmit={EditUserBio}>
            <TextInput
              placeholder="Your Bio"
              placeholderTextColor="#808e9b"
              style={styles.input}

              keyboardType="default"
              bind={UserBio}
              value={UserBio}
              onChangeText={(e) => setUserBio(e)}
            />
          </Dialog>
        </ScrollView>
      }
    </View>
  );
}

