import React from 'react';
import { View, Image, Text, TextInput, Alert, StatusBar, TouchableOpacity, StyleSheet, FlatList } from 'react-native';
import uuid from 'react-native-uuid';
import SelectDropdown from 'react-native-select-dropdown'
import AppLoading from 'expo-app-loading';


import * as ImagePicker from 'expo-image-picker';
import Login from '../Components/Login';
import User from '../Models/User';
import Constants from '../Models/Constants';
import Dropdown from '../Components/Dropdown';
import GlobalMethods from '../Models/GlobalMethods';




const SignUpScreen = (props) => {

  const [Product, SetProduct] = React.useState([]);
  const [Images, SetImages] = React.useState([]);
  const [Catagories, setCatagories] = React.useState([]);
  const [loaded, setLoaded] = React.useState(false);
  const [status, requestPermission] = ImagePicker.useCameraPermissions();
  const countries = ["Egypt", "Canada", "Australia", "Ireland"]
  const handleProduct = (name, value) => {
    const Catagories = [];
    // console.log(name+": "+value);
    SetProduct(prevState => ({
      ...prevState,
      [name]: value
    }));
  };
  const ValueChecker = (val) => val.length > 0
  const SignUpUser = () => {
    console.log(Product);
    console.log(Images);
  }
  // const SignUpUser = () => {
  //   let user = NewUser;
  //   user.id = uuid.v4();
  //   user.picture = 'uuid.v4()';
  //   if (Images === null) {
  //     Alert.alert("Please Choose Profile Photo");
  //     console.log("Please Choose Profile Photo");

  //   }
  //   else if (ValueChecker(user.firstName) && ValueChecker(user.lastName) &&
  //     ValueChecker(user.username) && ValueChecker(user.password)
  //   ) {


  //     let data = JSON.stringify({
  //       id: user.id,
  //       firstName: user.firstName,
  //       lastName: user.lastName,
  //       username: user.username,
  //       password: user.password,
  //       picture: user.picture,
  //       image: {
  //         uri: Images,
  //       }
  //     });
  //     let options = {
  //       method: 'POST',
  //       body: data,
  //       headers: {
  //         // Accept: 'application/json',
  //         // 'Content-Type': 'multipart/form-data',
  //         'Content-Type': 'application/json',
  //         'Authorization': 'Bearer e3fff09e-576b-4a08-8529-e88efd8eb5d5',
  //       },
  //     };

  //     fetch(Constants.SignUp, options).then(x => x.text())
  //       .then(x => {
  //         var validate = require('uuid-validate');
  //         if (validate(x)) {
  //           Alert.alert('Login Successfully', 'PLease wait');
  //           console.log('Logined');
  //           props.navigation.navigate('Profile', {
  //             UserId: x,
  //             name: NewUser.username,
  //           });
  //         }
  //         else {
  //           Alert.alert('Login Failed', 'x');
  //           console.log('Failes');
  //         }
  //       }).catch((error) => {
  //         console.log(error);
  //         // throw error;
  //       });
  //   }
  //   else {
  //     Alert.alert("Please fill all fields");
  //     console.log("Please fill all fields");
  //   }


  // };


  if (!loaded) {
    return (
      <AppLoading
        startAsync={async () => {
          let data = await GlobalMethods.GetAllCats();
          console.log(data);
          setCatagories(data);
        }}
        onFinish={() => setLoaded(true)}
        onError={console.warn}
      />
    );
  }
  const pickImage = async () => {
    if (status) {
      let result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.Images,
        base64: true,
        allowsEditing: true,
        aspect: [2, 2],
        quality: 1,
      });

      if (!result.cancelled) {
        // SetProfileTitle(result.title);
        SetImages(x => [...x, result.uri]);
        console.log(Images);
      }


    }

    else {
      console.warn("Give Camera Access to use this feature");
      ImagePicker.requestMediaLibraryPermissionsAsync(true);
    }
  };

  return (
    <Login Title="Create Product" loginUser={SignUpUser} Images={Images} navigation={props.navigation} ChooseImage={async () => pickImage()} >
      <TextInput
        placeholder="Name"
        placeholderTextColor="#808e9b"
        style={styles.input}
        autoCorrect={true}
        // autoCapitalize={false}
        autoCompleteType="name"
        keyboardType="default"
        textContentType="name"
        bind={Product.Name}
        Value={Product.Name}
        onChangeText={(e) => handleProduct('Name', e)}
      />
      <TextInput
        placeholder="Quantity"
        placeholderTextColor="#808e9b"
        style={styles.input}
        autoCorrect={true}
        // autoCapitalize={false}
        autoCompleteType="number"
        keyboardType="number-pad"
        bind={Product.Quantity}
        value={Product.Quantity}
        onChangeText={(e) => handleProduct('Quantity', e.replace(/[^0-9]/g, ''))}
      />
      <TextInput
        placeholder="Price"
        placeholderTextColor="#808e9b"
        style={styles.input}
        autoCorrect={true}
        // autoCapitalize={false}
        autoCompleteType="number"
        keyboardType="number-pad"
        bind={Product.Price}
        value={Product.Price}
        onChangeText={(e) => handleProduct('Price', e.replace(/[^0-9]/g, ''))}
      />

      <TouchableOpacity
        style={styles.input}
        disabled={(Image.length>=3)}
        onPress={pickImage.bind(this)}>
        <Text style={[styles.fpText, { alignSelf: 'center', }]}>Choose Images</Text>
      </TouchableOpacity>

      <Dropdown data={Catagories} onValueChange={e => handleProduct('catagoryId', e)} style={styles.input} label={'Select Catagory'} />

      {Images.length > 0 &&

        <View style={styles.imageContainer}>
          <FlatList numColumns={3} keyExtractor={(item,index)=>index} data={Images} renderItem={({ item }) => (
            <View style={{flex:1,}}>
              <Image
              style={styles.Image}
                source={{ uri: item }}
              />
              {console.log(item)}
            </View>
          )} />
        </View>
      }
    </Login>
  );
}


const styles = StyleSheet.create({

  input: {
    width: '100%',
    height: 50,
    backgroundColor: '#333',
    borderRadius: 6,
    marginTop: 10,
    paddingHorizontal: 10,
    fontSize: 16,
    color: '#808e9b',
  },
  fpText: {

    color: '#B33771',
    fontSize: 20,
    fontWeight: '900',
    marginTop: 10,
  },
  imageContainer: {
    width: '100%',
    // height: 50,
    
    backgroundColor: '#333',
    borderRadius: 6,
    marginTop: 10,
    paddingHorizontal: 10,
    // fontSize: 16,
    // color: '#808e9b',
  },
  Image: {
    width: 100,
    height: 100,
    borderRadius: 10,
    borderWidth: 2,
    alignSelf:'center',
    marginVertical: 10,
    borderColor: '#fff',
  },
});

export default SignUpScreen;
